import{_ as a,c as t,ag as s,o as r}from"./chunks/framework.CRouwKY9.js";const u=JSON.parse('{"title":"500错误处理","description":"","frontmatter":{},"headers":[],"relativePath":"work/fqa/debug.md","filePath":"work/fqa/debug.md","lastUpdated":1737701954000}'),o={name:"work/fqa/debug.md"};function n(i,e,l,c,d,p){return r(),t("div",null,e[0]||(e[0]=[s('<h1 id="_500错误处理" tabindex="-1">500错误处理 <a class="header-anchor" href="#_500错误处理" aria-label="Permalink to &quot;500错误处理&quot;">​</a></h1><div class="danger custom-block"><p class="custom-block-title">提示</p><p>500错误为服务器内部错误，所有服务端错误都会报500。如果只是反馈500错误，我们无法帮您解决问题，以下教程教大家如何获取具体错误信息。</p></div><h2 id="如何获取具体的500错误并反馈" tabindex="-1">如何获取具体的500错误并反馈 <a class="header-anchor" href="#如何获取具体的500错误并反馈" aria-label="Permalink to &quot;如何获取具体的500错误并反馈&quot;">​</a></h2><ul><li><strong>步骤1</strong>: 登录宝塔，点击【文件】，找到网站所有目录，打开项目一下的server/.env文件，把&quot;APP_DEBUG&quot;项的值改成true。</li></ul><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>APP_DEBUG = true</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p><img src="https://doc.chatmoney.cn/docs/images/general/php/debug/debug-1.png" alt=""></p><ul><li><strong>步骤2</strong>: 右键单击浏览器，单击【检查】-&gt;【Network(网络)】-&gt;【Fetch/XHR】,在左下角请求列表找到红色的请求，单击其中一行，单击【Preview(预览)】。蓝色框显示的信息，就是具体的错误信息，可将信息反馈给客服人员。<img src="https://doc.chatmoney.cn/docs/images/general/php/debug/debug-2.png" alt=""></li></ul><h2 id="通过错误信息自行解决问题" tabindex="-1">通过错误信息自行解决问题 <a class="header-anchor" href="#通过错误信息自行解决问题" aria-label="Permalink to &quot;通过错误信息自行解决问题&quot;">​</a></h2><h3 id="_1-系统用户权限问题" tabindex="-1">1.系统用户权限问题 <a class="header-anchor" href="#_1-系统用户权限问题" aria-label="Permalink to &quot;1.系统用户权限问题&quot;">​</a></h3><div class="danger custom-block"><p class="custom-block-title">错误信息</p><p>&quot;file_put_contents(/www/wwwroot/xxxxxx/server/runtime): Failed to open stream: Permission denied&quot;</p></div><p><img src="https://doc.chatmoney.cn/docs/images/general/php/debug/permission-1.png" alt=""></p><h4 id="原因" tabindex="-1">原因 <a class="header-anchor" href="#原因" aria-label="Permalink to &quot;原因&quot;">​</a></h4><p>此错误是由于系统文件权限引起报错，遇到类似的问题。</p><h4 id="解决方式" tabindex="-1">解决方式 <a class="header-anchor" href="#解决方式" aria-label="Permalink to &quot;解决方式&quot;">​</a></h4><p>打开项目【文件】，找到项目所在目录，鼠标上浮到server，这个时候出现了【权限】，单击【权限】，所有者设置为【www】，设置为勾上【应用到子目录】，单击【确定】。处理好以后，就不会有报错了。<img src="https://doc.chatmoney.cn/docs/images/general/php/debug/permission-2.png" alt=""><img src="https://doc.chatmoney.cn/docs/images/general/php/debug/permission-3.png" alt=""></p>',15)]))}const g=a(o,[["render",n]]);export{u as __pageData,g as default};
